// 'use strict'
/******************************************************************************
  STACK BASE
******************************************************************************/
// var { h, hsvg } = require('_h')
// var s           = require('_s')
/******************************************************************************
  CUSTOM
******************************************************************************/
const header      = require('_vcs-header')
const skillbox    = require('_vcs-skilltree/skillbox')
const promocode   = require('_vcs-promocode')
const lesson      = require('_vcs-lesson')
/******************************************************************************
  MAIN
******************************************************************************/
// module.exports = mainpage

function mainpage (db, engine) {


  /*****************************/
  // THEME + LOCAL CACHE + DEFAULT VALUES
  var VTREE = {
    header: '',
    content: '',
    footer: '',
    overlay: ''
  }
  var STATUS = {
    // UI STATUS
    inactive: undefined,
    mode: 'skillbox' // maybe part of routing
  }


  /*****************************/
  // INITIALIZE STYLING
  var x = s(`
    app {
      position: relative;
      min-height: 100vh;
      background-color: red;
    }
    esova--inactive {
      filter: blur(2px);
      -webkit-filter: blur(2px);
      background-color: red;
    }
    esova {
      position: relative;
      display: flex;
      flex-direction: column;
      min-height: 100vh;
      width: 100%;
      background-color: ${'#526E96'};
    }
    header {
      min-height: 165px;
    }
    content {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: stretch;
      flex-grow: 1;
    }
    footer {
      min-height: 165px;
    }
    overlay {
      position: absolute;
      top: 0;
      left: 0;
      display: flex;
      flex-direction: column;
      width: 100%;
      height: 100%;
    }
  `)


  /*****************************/
  // CHILD COMPONENTS & USED MODULES
  // @TODO
  // let them return stream$s that can be passed into hyperscript
  // so the custom VTREE CACHING becomes obsolete :-)
  promocode(db, { push: renderMainPage }) // show popup
  header(db, { push: renderMainPage })
  footer(db, { push: renderMainPage })

  skillbox(db, { push: renderMainPage })
  lesson(db, { push: renderMainPage })


  /*****************************/
  // RENDER COMPONENT
  function renderMainPage (/*error,*/ update) {
    // IDEA: old components should be able to answer with "no change"
    //       so that the last cached version can be used instead
    var childTree = (update||{}).value
    var childName = (update||{}).key
    // MAYBE UPDATE CHILDREN
    if (childName === 'skillbox') {
      VTREE.skillbox = childTree
    } else if (childName === 'lesson') {
      VTREE.lesson = childTree
    } else if (childName === 'footer') {
      VTREE.footer = childTree
    } else if (childName === 'header') {
      VTREE.header = childTree
    } else if (childName === 'promocode') { // OVERLAY
      VTREE.overlay = childTree
    }
    // SELECT CHILDREN
    VTREE.content = STATUS.mode === 'lesson' ? VTREE.lesson : VTREE.skillbox

    // RENDER
    var vtree = h('div', { className: x('app') }, [
      h('div', { className: x('overlay') }, VTREE.overlay),
      h('div', {
        className: x('esova', STATUS.inactive ? 'esova--inactive' : undefined)
      }, [
        h('div', { className: x('header') }, VTREE.header),
        h('div', { className: x('content') }, VTREE.content),
        h('div', { className: x('footer') }, VTREE.footer)
      ])
    ])
    // PUSH UPDATE
    engine.push({
      type: 'vtree',
      key: 'root',
      value: vtree
    })
  }


  /*****************************/
  // INCOMING COMMUNICATION CHANNELS
  db.on('put', function (key, value) { // SET by auth.js
    if (key === 'MODE') {
      if (value === 'lesson') { // goto lesson
        // @TODO: this component is using "lesson" and "promocode"
        //        and "maincomponent", so it needs to orchestrate
        // @XXX: Alternative: lesson description can switch itself into lesson
        //      thus => lesson is listening to MODE

        STATUS.mode = 'lesson'
      }
      else if (value === 'lesson denied') {
        STATUS.mode = 'skillbox'
        // @TODO: show overlay with message: "requirements unmet"
        //        FOR NOW: it's just commented out
        //
        // STATUS.inactive = true
        // renderMainPage()
        // db.put('OVERLAY', JSON.stringify({
        //   message: '',
        //   current
        // })) // triggers re-render
        // db.get('esova-lesson', function (error, value) {
        //   // auth(db, function noop (error, help) { if (error) ER(error) })
        //   debugger
        // })
        // STATUS.MODE = 'lesson denied'
      }
      else if (value === 'skillbox') {
        STATUS.mode = 'skillbox'
      }
      else { // goto skillbox (skilltree)
        debugger
        db.put('ERROR', 'MODE: '+value, ER)
        // @TODO: check what happens if i press the HOME button
      }
    }
    else if (key === 'esova-credentials') {
      var creds = JSON.parse(value)
      debugger
      if (creds && typeof creds.status === 'string' && creds.status!=='ok') {
        console.log('AUTH FAIL :-/')
        if (creds.status === 'unmet requirements') {
          alert('forbidden')
          // => show notification
          STATUS.inactive = true
          renderMainPage()

          debugger

          db.put('OVERLAY', value) // triggers re-render
        } else if (creds.status === 'no valid promocode') {
          // @TODO:
          // a certain STATUS can affect multiple components
          // => 1. an overlay showing/revealing itself
          // => 2. existing component changing appearance (e.g. blurring)
          STATUS.inactive = true
          renderMainPage()
          db.put('OVERLAY', value) // triggers re-render
        } else if (creds.status === 'no valid token') {
          renderMainPage()
        } else if (creds.status === 'could not store credentials') {
          renderMainPage()
        } else if (creds.status === 'couldnt get "auth code" from github') {
          renderMainPage()
        }
      } else {
        console.log('AUTH SUCCESSFUL :-)')
        STATUS.inactive = false
        renderMainPage() // @TODO: is a re-render here necessary?
      }
    }
  })


  /*****************************/
  // INITIALIZE
  console.log('--------------------------------')
  console.log('INITIALIZE: mainpage() component')
  console.log('--------------------------------')
  return renderMainPage


  /*****************************/
  // HELPER
  function ER (error) {
    // @TODO
    // default error logging failed
    // => try to learn about error via ajax or some other technique
    // => if it fails inform user instead and hope he reports it in chat
    if (error) {
      console.error('couldnt store ERROR event')
      console.error(error)
    }
  }


}
