var h = require('virtual-dom/h')

module.exports = NotFoundComponent

function NotFoundComponent () {
  var STATE = {
    data: { counter:0 },
    action() {
      console.log('PLUS!')
    }
  }
  return function render404 () {
    var vtree = (
      h('div', [  // h('div', 'not found')
        h('h1', '404 - not found'),
        h('h3', 'clicked ' + STATE.data.counter + ' times'),
        h('button', { onclick: onclick }, 'click me!'),
        h('br'),
        h('a', { href: '/' }, 'go to /'),
        h('a', { href: '/#asdf' }, 'go to /#asdf')
      ])
    )
    return vtree
  }
  function onclick () {
    STATE.data.counter = STATE.data.counter+1
    STATE.action('plus', STATE)
  }
}
